(def ops (slurp "input.txt"))

(def input "")

(defn freqs [input]
  (->> input
       (map second)
       frequencies))

(defn calculate [ops input]
  (let [ops-in-out-seq (map (fn [[_ in out]] {:in in :out out}) (re-seq #"(\S+)\s=>\s(\S+)" ops))
        replacements (map #(clojure.string/replace input (:in %) (:out %)) ops-in-out-seq)
        unique-reps (distinct replacements)]
    unique-reps))
